program cuse cont_moduse postscript integer,parameter :: dim = 400!integer,parameter :: dim = 20 real              :: func(dim,dim) integer           :: i,j,iloc pi = 4.0*atan(1.0) do i=1,dim  do j=1,dim   func(i,j) = sin(pi*float(i)/40.0)*cos(pi*float(j)/40.0)*sin(pi*float(i+j)/80.0)  end do end do! allocate(Cdata(dim*dim)) do i=1,dim  do j=1,dim   iloc = (j-1)*dim+i   Cdata(iloc) = func(i,j)  end do end do  PS%psname = 't.ps' call PS_openfile call PS_move(0.0,0.0) write (psunit,*) '0.5 0.5 scale'! Cparam%nx = dim Cparam%ny = dim Cparam%x0 = 2.00 Cparam%y0 = 2.00 Cparam%dx = 0.0125 Cparam%dy = 0.0125 Cparam%distort = reshape( (/ 1.0, 0.25, -0.5, 1.0 /), (/2,2/) ) do i=-5,5   Cparam%level = float(i)/5.0   call contour end do call PS_closefileend program